local HttpService = game:GetService("HttpService") local secretKey = "Sigmaboy123" local function xorCrypt(data, key) local result = {} for i = 1, #data do local byte = string.byte(data, i) local kByte = string.byte(key, ((i - 1) % #key) + 1) table.insert(result, string.char(bit32.bxor(byte, kByte))) end return table.concat(result) end local function toHex(str) return (str:gsub(".", function(c) return string.format("%02x", string.byte(c)) end)) end local function fromHex(hex) return (hex:gsub("..", function(cc) return string.char(tonumber(cc, 16)) end)) end local function loade(input) local encrypted = xorCrypt(input, secretKey) local hexEncrypted = toHex(encrypted) setclipboard(hexEncrypted) end local function load(input) local decrypted = xorCrypt(fromHex(input), secretKey) loadstring(decrypted)() end
